import React, { Component } from "react";
import { getPrices } from "./getPrices";


class Table extends Component {
  state = {
    origin: "011",
    destination: "011",
    plan: "FaleMais30",
    time: 20,
    withPlan: null,
    withoutPlan: null
  };
  getPlanOptions = () =>
    ["FaleMais30", "FaleMais60", "FaleMais120"].map(plan => (
      <option key={plan} value={plan}>{plan}</option>
    ));

  getDDDOptions = () =>
    ["011", "016", "017", "018"].map(ddd => <option key={ddd} value={ddd}>{ddd}</option>);

  setOrigin = e => this.setState({ origin: e.target.value });
  setDestination = e => this.setState({ destination: e.target.value });
  setPlan = e => this.setState({ plan: e.target.value });
  setTime = e => this.setState({ time: e.target.value });

  onSubmit = () => {
    const { origin, destination, plan, time } = this.state;

    const response = getPrices(origin, destination, plan, time);

    this.setState({
      withPlan: response.withPlan,
      withoutPlan: response.withoutPlan
    });
    this.props.onSubmit(getPrices);
  };

  render() {
    return (
      <table>
        <thead>
          <tr>
            <th>DDD Origem</th>
            <th>DDD Destino</th>
            <th>Plano FaleMais</th>
            <th>Tempo em minutos</th>
            <th>Valor sem FaleMais</th>
            <th>Valor com FaleMais</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>
              <select name="origin" onChange={this.setOrigin}>
                {this.getDDDOptions()}
              </select>
            </td>
            <td>
              <select name="destination" onChange={this.setDestination}>
                {this.getDDDOptions()}
              </select>
            </td>
            <td>
              <select name="plan" onChange={this.setPlan}>
                {this.getPlanOptions()}
              </select>
            </td>
            <td>
              <input type="number" min="1" onChange={this.setTime} />
            </td>
            <td>{this.state.withoutPlan}</td>
            <td>{this.state.withPlan}</td>
          </tr>
        </tbody>
      </table>
    );
  }
}

export default Table;
