import React, { Component } from "react";
import Plans from "../Plans";
import DDD from "../DDD";
import Time from "../Time";

class Table extends Component {
  state = {
    origin: null,
    destination: null
  };

  getDDDOptions = notAvailableDDD =>
    ["011", "016", "017", "018"]
      .filter(ddd => ddd !== notAvailableDDD)
      .map(ddd => <option value={ddd}>{ddd}</option>);

  setOrigin = (e) => this.setState({ origin: e.target.value })
  setDestination = (e) => this.setState({ destination: e.target.value })

  render() {
    const { origin, destination } = this.state;
    return (
      <table>
        <tr>
          <th>DDD Origem</th>
          <th>DDD Destino</th>
          <th>Plano FaleMais</th>
          <th>Tempo em minutos</th>
          <th>Valor sem FaleMais</th>
          <th>Valor com FaleMais</th>
        </tr>
        <tr>
          <td>
            <select name="origin" onChange={this.setOrigin}>{this.getDDDOptions(destination)}</select>
          </td>
          <td>
            <select name="destination" onChange={this.setDestination}>{this.getDDDOptions(origin)}</select>
          </td>
          <td>
            <select class="Plans" />
          </td>
          <td>
            <select class="Time" />
          </td>
          <td>

          </td>
          <td>

          </td>
        </tr>
      </table>
    );
  }
}

export default Table;
