import React, { Component } from "react";
import Plans from "../Plans";
import DDD from "../DDD";
import Time from "../Time";
import { getPrices } from './getPrices';

class Table extends Component {
  state = {
    origin: '011',
    destination: '011',
    plan: 'FaleMais30'
  };
  getPlanOptions = () =>
    ["FaleMais30", "FaleMais60", "FaleMais120"]
      .map(plan => <option value={plan}>{plan}</option>);


  getDDDOptions = () =>
    ["011", "016", "017", "018"]
      .map(ddd => <option value={ddd}>{ddd}</option>);

  setOrigin = (e) => this.setState({ origin: e.target.value })
  setDestination = (e) => this.setState({ destination: e.target.value })


  onSubmit = () => {
    const { origin, destination } = this.state;

    const obj = getPrices(origin, destination, null, 30);

    console.log(obj)


  }

  render() {
    return (
      <table>
        <tr>
          <th>DDD Origem</th>
          <th>DDD Destino</th>
          <th>Plano FaleMais</th>
          <th>Tempo em minutos</th>
          <th>Valor sem FaleMais</th>
          <th>Valor com FaleMais</th>
        </tr>
        <tr>
          <td>
            <select name="origin" onChange={this.setOrigin}>{this.getDDDOptions()}</select>
          </td>
          <td>
            <select name="destination" onChange={this.setDestination}>{this.getDDDOptions()}</select>
          </td>
          <td>
            <select name="plan" onChange={this.setPlan}>{this.getPlanOptions()}</select>
          </td>
          <td>
            <input className="Time" type = "number" min="1" />
          </td>
          <td>
            <button type="button" onClick={this.onSubmit}>Submeter</button>
          </td>
          <td>

          </td>
        </tr>
      </table>
    );
  }
}

export default Table;
