import React, { Component } from 'react';

class Button extends Component {
  state = {
    aparece: false,
  }

  toggleAparece = () => this.setState(prevState => ({ aparece: !prevState.aparece }))

  render() {
    console.log(this.props);
    return (
      <React.Fragment>
      <button type="button" onClick={this.toggleAparece}>Calcular Valores</button>
      {this.state.aparece && <p>OI</p>}
      </React.Fragment>
    );
  }
}

export default Button;
